const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const c=e.getAttribute("href").substr(1);document.getElementById(c).scrollIntoView({behavior:"smooth",block:"start"})}));window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".menu-header"),t=document.querySelectorAll(".menu-header__link"),c=document.querySelector(".header__burger"),o=document.querySelector("body");c.addEventListener("click",()=>{c.classList.toggle("active"),e.classList.toggle("active"),o.classList.toggle("lock")}),t.forEach(t=>{t.addEventListener("click",()=>{c.classList.toggle("active"),e.classList.toggle("active"),o.classList.toggle("lock")})})});